INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(1, 'Vasos de plantas', 'Use água, esponja e sabão para limpeza. Deposite areia na vasilha sob o vaso a cada limpeza.', 'http://aedesapp.com/arquivo/vaso.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(2, 'Ralos', 'Limpe-os jogando água sanitária. Mantenha-os vedados caso não os utilize.', 'http://aedesapp.com/arquivo/ralos.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(3, 'Recipientes com água', 'Use água, esponja e sabão para limpeza. (Jarras, garrafas, potes e baldes).', 'http://aedesapp.com/arquivo/recipientes_agua.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(4, 'Caixa d’água', 'Use água, sabão e água sanitária para a limpeza. Mantenha a mesma tampada durante todo o tempo.', 'http://aedesapp.com/arquivo/caixa_dgua.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(5, 'Bebedouros de Animais', 'Use água, esponja e sabão para limpeza. Mantenha-os limpos, trocando a água diariamente.', 'http://aedesapp.com/arquivo/bebedouro_animais.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(6, 'Piscinas', 'Use água, sabão e água sanitária para limpeza. O cloro deve estar sempre no nível adequado, tratando a água. Manter coberta, caso não esteja utilizando.', 'http://aedesapp.com/arquivo/piscinas.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(7, 'Calhas', 'Remova tudo que possa impedir a passagem da água.', 'http://aedesapp.com/arquivo/calhas.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(8, 'Bromélias (Planta)', 'Dilua uma colher (sopa) de água sanitária em 1 litro de água limpa e regue-as. Eliminá-las poderia afetar o equilíbrio ecológico, portanto, o melhor é preservar de maneira correta.', 'http://aedesapp.com/arquivo/bromelias_plantas.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(9, 'Pneus Velhos', 'Mantenha-os em lugar coberto ou fure-os. Caso queira descartá-los, separe-os para serem levados pela coleta de lixo. Nunca os jogue em terrenos baldios.', 'http://aedesapp.com/arquivo/pneus.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(10, 'Sacos de Lixo', 'Deve ser bem fechado e DIARIAMENTE depositado no lugar adequado para ser recolhidos pela coleta de lixo.', 'http://aedesapp.com/arquivo/lixos.jpg');
INSERT INTO foco (codigo, nome, como_limpar, foto) VALUES(11, 'Geladeiras', 'A cafeína é um método natural, não prejudicial e fatal contra a dengue. Deposite na gaveta, abaixo, no exterior da geladeira, 4 colheres de sopa de borra de café, para cada 300 ml de água. ', 'http://aedesapp.com/arquivo/ar_condicionado.jpg');
